<div class="container" *ngFor="let expense of expenses">
  <app-expense-menu-card
  [name]="expense.name"
  [amount]="expense.amount"
  [currency]="expense.currency"
  [distributionType]="expense.distributionType"
  [groupName]="expense.group.name"
  (click)="navigate(expense.group.id)"
  ></app-expense-menu-card>
</div>

<div class="fixedButton">
  <button type="button" class="btn btn-primary">Add expense</button>
</div>
