import { Component } from '@angular/core';

@Component({
  selector: 'app-groups',
  templateUrl: './groups.component.html',
  styleUrls: ['./groups.component.css'],
})
export class GroupsComponent {
  groups = [
    {
      id: 1,
      amount: 300,
      currency: 'EUR',
      groupName: 'Görögország',
    },
    {
      id: 2,
      amount: 25000,
      currency: 'HUF',
      groupName: 'Albérlet',
    },
    {
      id: 3,
      amount: 0,
      currency: 'HUF',
      groupName: 'Kiscsalád',
    },
  ];
}
