import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ExpenseService } from 'src/app/services/expense.service';

@Component({
  selector: 'app-expense-details',
  templateUrl: './expense-details.component.html',
  styleUrls: ['./expense-details.component.css'],
})
export class ExpenseDetailsComponent implements OnInit {
  payer = {
    id: 1,
    username: 'fancyUsername21',
    fullName: 'Horváth Benedek',
  };
  amount = 5000;
  currency = 'HUF';
  distribution = 'exact_amounts';
  expenseMembers = [
    {
      id: 1,
      username: 'fancyUsername21',
      fullName: 'Horváth Benedek',
      amount: 1000,
    },
    {
      id: 2,
      username: 'fancyUsername22',
      amount: 1000,
    },
    {
      id: 3,
      username: 'fancyUsername23',
      fullName: 'Herman Csilla',
      amount: 3000,
    },
  ];

  id!: number;
  expenseDetails: any;
  debts: any;

  constructor(
    private expenseService: ExpenseService,
    private route: ActivatedRoute,
    private location: Location,
  ) {}

  ngOnInit(): void {
    this.id = +this.route.snapshot.paramMap.get('id')!;
    this.expenseService.getExpenseById(this.id).subscribe((result) => {
      this.expenseDetails = result;
      this.debts = this.expenseDetails.debts;
      console.log('expenseDetails: ', this.expenseDetails);
      console.log('debts: ', this.debts);
    });
  }

  goBack() {
    this.location.back();
  }
}
<div class="row">
  <div class="col-auto">
    <button
      type="button"
      class="btn btn-primary rounded-circle"
      title="Back"
      (click)="goBack()"
    >
      <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
    </button>
  </div>
  <div class="col">
    <div class="row">
      <div class="text-center text-muted">Expense details</div>
    </div>
    <div class="row">
      <div class="text-center">
        <h2>
          {{ expenseDetails.expenseName }}
        </h2>
      </div>
    </div>
  </div>
</div>
<div class="row">
  <div class="col">
    <div class="row" *ngFor="let debt of debts">
      <app-debt-card
      [amount]="debt.amount"
      [currency]="debt.currency"
      [name]="debt.user.name"
      ></app-debt-card>
    </div>
  </div>
  <div class="col"></div>
</div>
