import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-expenses',
  templateUrl: './expenses.component.html',
  styleUrls: ['./expenses.component.css'],
})
export class DebtsComponent implements OnInit{
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }
  expenses = [
    {
      id: 1,
      expenseAmount: 2500,
      youOwe: 300,
      currency: 'HUF',
      groupName: 'Borok',
      payerName: 'Bede',
      distributionType: 'equally',
    },
    {
      id: 2,
      expenseAmount: 10000,
      youOwe: 2000,
      currency: 'HUF',
      groupName: 'Jegyek',
      payerName: 'Csilla',
      distributionType: 'equally',
    },
    {
      id: 1,
      expenseAmount: 5500,
      youOwe: 900,
      currency: 'HUF',
      groupName: 'Fagyi',
      payerName: 'Peti',
      distributionType: 'exactly',
    },
  ];
}
<div class="container" *ngFor="let expense of expenses">
  <app-expense-card
    [id]="expense.id"
    [expenseAmount]="expense.expenseAmount"
    [currency]="expense.currency"
    [payerName]="expense.payerName"
    [youOwe]="expense.youOwe"
    [distributionTypeKey]="expense.distributionType"
  ></app-expense-card>
</div>

<div class="fixedButton">
  <button type="button" class="btn btn-primary">Add expense</button>
</div>
