import { Component, OnInit } from '@angular/core';
import { DebtService } from 'src/app/services/debt.service';
import { DebtDTO } from '../dto';

@Component({
  selector: 'app-debts',
  templateUrl: './debts.component.html',
  styleUrls: ['./debts.component.css']
})
export class DebtsComponent implements OnInit{
  debts!: DebtDTO[];


  constructor(private debtService: DebtService){}

  ngOnInit(): void {
    this.debtService.getMyDebts().subscribe(result=>{
      this.debts = result;
      console.log("result: ", this.debts);
    })
  }

}
<div class="row">
  <div class="row mb-4">
    <div class="col">
      <div class="d-flex justify-content-center">
        <span class="display-4">Debts</span>
      </div>
    </div>
  </div>
  <div class="row d-flex justify-content-center">

      <div class="col-md-10" *ngFor="let debt of debts">
        <app-debts-menu-card
          [userId]="debt.userId"
          [amount]="debt.amount"
          [currency]="debt.currency"
          [payerId]="debt.payerId"
          [payerName]="debt.payerName"
          [groupId]="debt.groupId"
          [groupName]="debt.groupName"
          [expenseId]="debt.expenseId"
          [expenseName]="debt.expenseName"
        ></app-debts-menu-card>

      </div>

  </div>
</div>
