import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { BalanceService } from 'src/app/services/balance.service';
import { BalanceDTO } from '../dto/balance.dto';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { SettleUpComponent } from '../settle-up/settle-up.component';

@Component({
  selector: 'app-balance',
  templateUrl: './balance.component.html',
  styleUrls: ['./balance.component.css'],
})
export class BalanceComponent implements OnInit {
  balances!: BalanceDTO[];
  constructor(
    private balanceService: BalanceService,
    private route: ActivatedRoute,
    private location: Location,
    private modalService: NgbModal
  ) {}
  ngOnInit(): void {
    const groupId = parseInt(this.route.snapshot.paramMap.get('id') ?? '');
    this.balanceService
      .getMyBalancesByGroupId(groupId)
      .subscribe((response) => {
        console.log('balances: ', response);
        this.balances = response;
      });
  }

  goBack() {
    this.location.back();
  }

  openModal(){
    const modalRef = this.modalService.open(SettleUpComponent, centered: true);
		modalRef.componentInstance.name = 'World';
  }
}
<div class="row mb-4 mt-2">
  <div class="col-auto">
    <button
      type="button"
      class="btn btn-primary rounded-circle"
      title="Back"
      (click)="goBack()"
    >
      <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
    </button>
  </div>
  <div class="col">
    <h1 class="text-center">Wich balance do you want to settle?</h1>
  </div>
</div>
<div class="row d-flex justify-content-center">
  <div class="col-md-8">
    <ng-container *ngFor="let balance of balances">
      <ng-container *ngFor="let youOweByCurrency of balance.youOwe">
        you owe
        <app-balance-card
        [payerId]="balance.you.id"
        [payerUsername]="balance.you.username"
        [recieverId]="balance.other.id"
        [recieverUsername]="balance.other.username"
        [amount]="youOweByCurrency.amount"
        [currency]="youOweByCurrency.currency"
       (click)="openModal()"

        ></app-balance-card>
      </ng-container>
      <ng-container *ngFor="let youAreOwedByCurrency of balance.youAreOwed">
        you are owed
        <app-balance-card
       (click)="openModal()"
        ></app-balance-card>
      </ng-container>
    </ng-container>
  </div>
</div>
