import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ExpenseService } from 'src/app/services/expense.service';
import { GroupService } from 'src/app/services/group.service';
import { distributionTypesEnumMappings as distributionTypesEnumMappings } from '../enum';

@Component({
  selector: 'app-create-expense',
  templateUrl: './create-expense.component.html',
  styleUrls: ['./create-expense.component.css'],
})
export class CreateExpenseComponent implements OnInit {
  formData = {
    expenseName: '',
    description: '',
    distributionType: ''
  };
  groupMembers!: { id: number; username: string }[];
  selectedGroupMembers: number[] = [];
  groupId!: number;
  categories!: string[];
  distributionTypes: string[] = Object.values(distributionTypesEnumMappings);
  currencies!: string[];
  constructor(
    private expenseService: ExpenseService,
    private groupService: GroupService,
    private location: Location,
    private route: ActivatedRoute
  ) {}
  ngOnInit(): void {
    this.expenseService.getExpenseCategories().subscribe((result) => {
      this.categories = result;
      console.log(this.categories);
    });
    const groupId = this.route.snapshot.paramMap.get('id');
    this.groupId = parseInt(groupId!);
    console.log('Group ID:', this.groupId);

    this.groupService.getGroupMembers(this.groupId).subscribe((result) => {
      this.groupMembers = result;
    });

    this.expenseService.getCurrencies().subscribe((result) => {
      this.currencies = result;
    });
    // this.expenseService.getExpenseDistributions().subscribe(result=>{
    //   this.distributionTypes = result;
    // })
  }

  toggleSelection(userId: number) {
    if (this.selectedGroupMembers.includes(userId)) {
      this.selectedGroupMembers = this.selectedGroupMembers.filter(
        (item) => item !== userId
      );
    } else {
      this.selectedGroupMembers.push(userId);
    }
    console.log("this.selectedGroupMembers: ",this.selectedGroupMembers);

  }

  goBack() {
    this.location.back();
  }

  submitForm(): void {
    console.log('Form data:', this.formData);
    // Add your form submission logic here
  }
}
<div class="row mb-4 mt-4">
  <div class="col-auto">
    <button
      type="button"
      class="btn btn-primary rounded-circle"
      title="Back"
      (click)="goBack()"
    >
      <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
    </button>
  </div>
  <div class="col text-center">
    <span class="display-4">Add new expense</span>
  </div>
</div>
<div class="row">
  <div class="col"></div>
  <div class="col-md-5">
    <form>
      <div class="form-group mb-2">
        <label for="name">Expense name</label>
        <input
          type="text"
          class="form-control"
          id="name"
          name="name"
          [(ngModel)]="formData.expenseName"
        />
      </div>
      <div class="row">
        <div class="col">
          <label for="name">Amount</label>
          <input type="number" inputmode="numeric" class="form-control" placeholder="Spent amount" id="amount" name="amount">
        </div>
        <div class="col">
          <label for="currency">Currency</label>
          <select type="text" class="form-control" id="currency" name="currency">
            <ng-container *ngFor="let currency of currencies">
              <option value="currency">{{currency}}</option>
            </ng-container>
          </select>
        </div>
      </div>
      <div class="form-group">
        <label for="category">Expense category</label>
        <select
          class="form-select"
          aria-label="Default select example"
          id="category"
          name="category"
        >
          <ng-container *ngFor="let category of categories">
            <option value="1">{{ category }}</option>
          </ng-container>
        </select>
      </div>
      <div class="form-group">
        <label for="description">Description</label>
        <textarea
          name="description"
          title="description"
          id=""
          class="form-control"
          placeholder="Give a description..."
          [(ngModel)]="formData.description"
        ></textarea>
      </div>
      <div class="row">
        <div class="col-auto">
          <p>Paid by</p>
        </div>
        <div class="col">
          <select class="form-select" title="payer">
            <ng-container *ngFor="let groupMember of groupMembers">
              <option id="{{groupMember.id}}">{{groupMember.username}}</option>
            </ng-container>
          </select>
        </div>
        <div class="col-auto">
          <p>and split</p>
        </div>
        <div class="col">
          <select class="form-select" title="splittingMethod" [(ngModel)]="formData.distributionType">
            <ng-container *ngFor="let distributionType of distributionTypes">
              <option id="{{distributionType}}">{{distributionType}}</option>
            </ng-container>
          </select>
        </div>
      </div>
      <div class="row">
        <div class="col">
          <ng-container *ngIf="formData.distributionType === 'equal'">
          </ng-container>
          <ng-container *ngFor="let groupMember of groupMembers">
            <div class="form-check form-check-reverse card mb-2">
              <div class="card-body d-flex justify-content-between">
                <label class="form-check-label" for="flexCheckDefault">
                  {{groupMember.username}}
                </label>
                <input class="form-check-input" type="checkbox" [value]="groupMember.id" id="flexCheckDefault" (change)="toggleSelection(groupMember.id)">
              </div>
            </div>
          </ng-container>
        </div>
      </div>
      <button type="submit" class="btn btn-primary" (click)="submitForm()">
        Create
      </button>
    </form>
  </div>
  <div class="col"></div>
</div>
