import { Component } from '@angular/core';
import { Observable, OperatorFunction, catchError, debounceTime, distinctUntilChanged, of, switchMap, tap } from 'rxjs';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-find-user-typeahead',
  templateUrl: './find-user-typeahead.component.html',
  styleUrls: ['./find-user-typeahead.component.css']
})
export class FindUserTypeaheadComponent {
  model: any;
  constructor(private userService: UserService){}

  search: OperatorFunction<string, readonly {id: number, username: string}[]> = ((searchTerm: Observable<string>)=>
  searchTerm.pipe(
    debounceTime(200),
    distinctUntilChanged(),
    tap(() => (this.searching = true)),
    switchMap((term)=>
     this.userService.searchUser(term).pipe(
      tap(() => (this.searchFailed = false)),
					catchError(() => {
						this.searchFailed = true;
						return of([]);
					}),
     )
    )
  )
  )
}
