import { Component } from '@angular/core';

@Component({
  selector: 'app-groups',
  templateUrl: './groups.component.html',
  styleUrls: ['./groups.component.css'],
})
export class GroupsComponent {
  groups = [
    {
      id: 1,
      amount: 300,
      currency: 'EUR',
      groupName: 'Görögország',
    },
    {
      id: 2,
      amount: 25000,
      currency: 'HUF',
      groupName: 'Albérlet',
    },
    {
      id: 3,
      amount: 0,
      currency: 'HUF',
      groupName: 'Kiscsalád',
    },
  ];
}
<div class="container" *ngFor="let group of groups">
  <app-card
    [id]="group.id"
    [amount]="group.amount"
    [currency]="group.currency"
    [groupName]="group.groupName"
  ></app-card>
</div>

<div class="fixedButton">
  <button type="button" class="btn btn-primary">Add group</button>
</div>
