import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { GroupService } from 'src/app/services/group.service';

@Component({
  selector: 'app-group-details',
  templateUrl: './group-details.component.html',
  styleUrls: ['./group-details.component.css'],
})
export class GroupDetailsComponent implements OnInit {
  id!: number;
  details: any;
  //TODO: lekérdezni, a user mennyivel tartozik az expenseben
  expenses!: {
    id: number,
    name: string;
    amount: number;
    currency: string;
    category: string;
    distribution: string;
    payerName: string
  }[];
  members!: {
    id: number;
    username: string;
    firstName: string;
    lastName: string;
  }[];
  constructor(
    private groupService: GroupService,
    private route: ActivatedRoute,
    private location: Location,
  ) {}

  ngOnInit(): void {
    this.id = +this.route.snapshot.paramMap.get('id')!;
    this.groupService.getGroupById(this.id).subscribe((result) => {
      this.details = result;
      this.expenses = this.details.expenses;
      this.members = this.details.members;
    });
  }
  goBack() {
    this.location.back();
    }
}
<div class="container"></div>
<div class="row mb-4">
  <div class="col-auto">
    <button type="button" class="btn btn-primary rounded-circle" title="Back" (click)="goBack()">
      <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
    </button>
  </div>
  <div class="col">
    <h1 class="text-center">{{ details.name }}</h1>
  </div>
  <div class="col-auto">
    <div class="row mb-4">

      <button class="btn btn-primary">Edit group</button>
    </div>
  </div>
</div>
<div class="row">
  <div class="col-md">
    <div class="row">
      <div class="card">
        <div class="card-body">
          <div class="card-title text-center">
            <h5>Members:</h5>
          </div>
          <div class="card-text">
            <div *ngFor="let member of members">
              <div class="row mb-2">
                <div class="col-md text-center">
                  <div
                    *ngIf="member.firstName && member.lastName; else elseBlock"
                  >
                    {{ member.firstName }} {{ member.lastName }}
                  </div>
                </div>
                <ng-template #elseBlock>
                  {{ member.username }}
                </ng-template>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  <div class="col-md-8">
    <div class="cad-deck" *ngFor="let expense of expenses">
      <app-expense-card
        [id]="expense.id"
        [currency]="expense.currency"
        [distributionType]="expense.distribution"
        [expenseAmount]="expense.amount"
        [expenseName]="expense.name"
        [payerName]="expense.payerName"
      ></app-expense-card>
    </div>
  </div>
  <div class="col-md">
    <p>TODO: itt lesznek a utility gombok asszem</p>
  </div>
</div>
<div class="fixedButton">
  <button type="button" class="btn btn-primary">Add expense</button>
</div>
