import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { GroupService } from 'src/app/services/group.service';

@Component({
  selector: 'app-group-details',
  templateUrl: './group-details.component.html',
  styleUrls: ['./group-details.component.css'],
})
export class GroupDetailsComponent implements OnInit {
  id!: number;
  details: any;
  expenses!: {
    name: string;
    amount: number;
    currency: string;
    category: string;
    distribution: string;
    payer: {
      id: number;
      username: string;
      firstName: string;
      lastName: string;
    };
  }[];
  members!: {
    id: number;
    username: string;
    firstName: string;
    lastName: string;
  }[];
  constructor(
    private route: ActivatedRoute,
    private groupService: GroupService
  ) {}
  ngOnInit(): void {
    this.id = +this.route.snapshot.paramMap.get('id')!;
    this.groupService.getGroupById(this.id).subscribe((result) => {
      this.details = result;
      this.expenses = this.details.expenses;
      this.members = this.details.members;
    });
  }
}
<div class="container">
  <div class="row justify-content-center mb-4">
    <h1 class="text-center">{{ details.name }}</h1>
  </div>
  <div class="row">
    <div class="col">
      <p>szia</p>
      <ngb-table [rows]="data">
        + 
        +   <ng-template ngbTableHead>
        +     <th>Name</th>
        +     <th>Price</th>
        +     <th>Availability</th>
        +   </ng-template>
        +  
        +   <ng-template ngbTableBodyRow let-row>
        +     <td>{{ row.name }}</td>
        +     <td>USD {{ row.price }}</td>
        +     <td>{{ row.availability }}</td>
        +   </ng-template>
        + 
        + </ngb-table>
    </div>
    <div class="col-md-8">
      <app-expense-card></app-expense-card>
    </div>
    <div class="col">
      <p>hé te ott</p>
    </div>
  </div>
</div>
