import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { UserDTO } from './dto';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css'],
})
export class UserComponent implements OnInit {
  userData!: UserDTO;

  constructor(private userService: UserService) {}
  ngOnInit(): void {
    this.userService.getMe().subscribe((response) => this.userData = response);
  }
}
<div class="row mb-4">
  <div class="col d-flex justify-content-center">
      <span class="display-4">My profile</span>
  </div>
</div>
<div class="row ">
    <div class="container-fluid d-flex justify-content-center align-items-center vh-100"></div>
    <div class="col d-column justify-content-center">
        <div class="row">
            <div class="col">

            </div>
            {{userData.username}}
        </div>
        <div class="row">
            <div class="col">

            </div>
            {{userData.firstName}}

        </div>
        <div class="row">
            <div class="col">

            </div>
            {{userData.lastName}}

        </div>
        <div class="row">
            <div class="col">

                {{userData.email}}
            </div>

        </div>
    </div>
</div>
