import { Location } from '@angular/common';
import { AfterContentInit, AfterViewInit, Component, OnInit, TemplateRef } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Route } from '@angular/router';
import { GroupService } from 'src/app/services/group.service';
import { GroupDTO } from '../dto/group.dto';
import { UserService } from 'src/app/services/user.service';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-edit-group',
  templateUrl: './edit-group.component.html',
  styleUrls: ['./edit-group.component.css'],
})
export class EditGroupComponent implements OnInit {
  id!: number;
  isCollapsed = true;
  groupDetails!: GroupDTO;
  editGroupForm = new FormGroup({
    groupName: new FormControl('', Validators.required),
    userIds: new FormControl<number[]>([]),
  });
  users!: { id: number; username: string; }[];
  selectedUsers: number[] = [];
  constructor(
    private location: Location,
    private route: ActivatedRoute,
    private groupService: GroupService,
    private userService: UserService,
    private modalService: NgbModal
  ) {}


  ngOnInit(): void {
    this.id = +this.route.snapshot.paramMap.get('id')!;
    console.log('id: ', this.id);
    this.groupService
      .getGroupById(this.id)
      .subscribe((response) =>{ 
        this.groupDetails = response
        console.log(this.groupDetails.name);
        this.editGroupForm.get('groupName')?.setValue(this.groupDetails.name);
        const userIds = this.groupDetails.members.map((member) => member.id);
        this.editGroupForm.get('userIds')?.setValue(userIds);
        console.log(this.editGroupForm.get('userIds')?.value);
        this.userService.getUsers().subscribe(response=>{      
          this.users = response;
          console.log(this.users);
          this.users =  this.users.filter(user=>!this.groupDetails.members.some(member=>member.id === user.id))
          console.log(this.users);

        })
      })

  }

  toggleSelection(userId: number) {
    if (this.selectedUsers.includes(userId)) {
      this.selectedUsers = this.selectedUsers.filter((item) => item !== userId);
    } else {
      this.selectedUsers.push(userId);
    }
    this.editGroupForm.get('userIds')?.setValue(this.selectedUsers);
    console.log("selectedUsers: ", this.selectedUsers);

  }

  open(content: TemplateRef<any>) {
		this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title' }).result.then(
			(result) => {

			}
		);
	}

  isChecked(id: number){
   return  this.selectedUsers.find(userId=> userId === id) ? true : false;
  }
  submitForm() {
    throw new Error('Method not implemented.');
    }
  goBack() {
    this.location.back();
  }
  get groupName() {
    return this.editGroupForm.get('groupName');
  }
}
<div class="row mb-4 mt-2">
    <div class="col-auto">
      <button
        type="button"
        class="btn btn-primary rounded-circle"
        title="Back"
        (click)="goBack()"
      >
        <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
      </button>
    </div>
    <div class="col">
      <h1 class="text-center">Editgroup</h1>
    </div>
  </div>
  <div class="row">
  <div class="col"></div>
  <div class="col-md-5">
    <div class="row mb-2">
        <form [formGroup]="editGroupForm" (ngSubmit)="submitForm()">
            <div class="row mb-2">
              <div class="col">
                <div class="form-group">
                  <label for="groupName">Group name</label>
                  <input
                    type="text"
                    class="form-control"
                    id="groupName"
                    name="groupName"
                    formControlName="groupName"
                    required
                  />
                  <div
                    *ngIf="
                      groupName?.invalid && (groupName?.dirty || groupName?.touched)
                    "
                    class="alert alert-danger"
                  >
                    <div *ngIf="groupName?.errors?.['required']">
                      Name is required.
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div class="row mb-2">
              <div class="col">
                <label for="memberList">Members</label>
                  <ul class="list-group" id="memberList">
                      <ng-container *ngFor="let member of groupDetails.members">
                          <li class="list-group-item">
                              {{member.username}}
                          </li>
                      </ng-container>
                    </ul>
              </div>
            </div>
            <div class="row mb-2">
              <div class="col">
                <ng-container *ngIf="true">
                  <!-- TODO: ng bootstrap accordionba tenni, így összecsukható a rengeteg user? -->
                  <div>
                    <button
                      type="button"
                      class="btn btn-primary mb-2"
                      (click)="isCollapsed = !isCollapsed"
                      [attr.aria-expanded]="!isCollapsed"
                    >
                      Add users
                    </button>
                  </div>
                  <div #collapse="ngbCollapse" [(ngbCollapse)]="isCollapsed">
                    <ul class="list-group">
                      <ng-container *ngFor="let user of users">
                        <input type="checkbox" class="btn-check" id="checkbox{{user.id}}" autocomplete="off">
                        <label class="form-check-label" for="checkbox{{user.id}}">
                          <button type="button" class="list-group-item list-group-item-action" [ngClass]="{'active': isChecked(user.id)}" (click)="toggleSelection(user.id)">{{user.username}}</button>
                        </label>
                      </ng-container>
                    </ul>
                  </div>

                </ng-container>
              </div>
            </div>
            <button
              type="submit"
              class="btn btn-primary"
              [disabled]="!editGroupForm.valid"
            >
              Save
            </button>
          </form>
    </div>
    <button type="button" class="btn btn-danger" data-bs-toggle="modal"(click)="open(content)">
        Delete group
      </button>
  </div>
  <div class="col"></div>
</div>
<ng-template #content let-modal>
	<div class="modal-header">
		<h4 class="modal-title" id="modal-basic-title">Delete group {{groupDetails.name}}?</h4>
		<button type="button" class="btn-close" aria-label="Close" (click)="modal.dismiss('Cross click')"></button>
	</div>
	<div class="modal-body">
		If you delete the group, all the expenses in it well be lost as well.
	</div>
	<div class="modal-footer">
		<button type="button" class="btn btn-outline-dark" (click)="modal.close('Save click')">Save</button>
	</div>
</ng-template>

<button class="btn btn-lg btn-outline-primary" (click)="open(content)">Launch demo modal</button>
