import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { GetMyGroupsDTO } from '../group/dto';

@Injectable({
  providedIn: 'root',
})
export class GroupService {
  private baseUrl: string = 'http://localhost:3000/group';
  constructor(private http: HttpClient) {}

  getMyGroups() {
    return this.http.get<GetMyGroupsDTO[]>(this.baseUrl + '/mygroups');
  }
  getGroupDetailsById(id: number) {
    return this.http.get(this.baseUrl + `/${id}` + '/details');
  }
  getGroupMembers(groupId: number): Observable<{id: number, username: string}[]> {
    const result =  this.http.get<{id: number, username: string}[]>(this.baseUrl + `/${groupId}` + '/members');
    return result;
  }
}
