import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ExpenseService } from 'src/app/services/expense.service';
import { GroupService } from 'src/app/services/group.service';
import {
  AbstractControl,
  FormControl,
  FormGroup,
  ValidationErrors,
  ValidatorFn,
  Validators,
} from '@angular/forms';

@Component({
  selector: 'app-create-expense',
  templateUrl: './create-expense.component.html',
  styleUrls: ['./create-expense.component.css'],
})
export class CreateExpenseComponent implements OnInit {
  createGroupForm = new FormGroup({
    name: new FormControl('', [Validators.required]),
    payerId: new FormControl(-1, [Validators.required, Validators.min(0)]),
    groupId: new FormControl(-1, [Validators.min(0), Validators.required]),
    amount: new FormControl('', Validators.required),
    currency: new FormControl('', Validators.required),
    category: new FormControl('', Validators.required),
    distributionType: new FormControl('', Validators.required),
    description: new FormControl(''),
    userIds: new FormControl<number[]>(
      [],
      [Validators.required, Validators.minLength(1)]
    ),
    proportionalDebtsData: new FormControl<
      { userId: number; percent: number }[]
    >([], [percentSumValidator('distributionType')]),
  });

  groupMembers!: { id: number; username: string }[];
  selectedGroupMembers: number[] = [];
  proportionalMembers: { userId: number; percent: number }[] = [];
  groupId!: number;
  categories!: string[];
  distributionTypes!: string[];
  currencies!: string[];
  constructor(
    private expenseService: ExpenseService,
    private groupService: GroupService,
    private route: ActivatedRoute,
    private router: Router
  ) {}
  ngOnInit(): void {
    this.expenseService.getExpenseCategories().subscribe((result) => {
      this.categories = result;
    });
    const groupId = this.route.snapshot.paramMap.get('id');
    this.groupId = parseInt(groupId!);
    const groupIdControl = this.createGroupForm.get('groupId');
    if (groupIdControl !== null) {
      groupIdControl.setValue(this.groupId);
    }

    this.groupService.getGroupMembers(this.groupId).subscribe((result) => {
      this.groupMembers = result;
    });

    this.expenseService.getCurrencies().subscribe((result) => {
      this.currencies = result;
    });
    this.expenseService.getExpenseDistributions().subscribe((result) => {
      this.distributionTypes = result;
    });
  }

  toggleSelection(userId: number) {
    if (this.selectedGroupMembers.includes(userId)) {
      this.selectedGroupMembers = this.selectedGroupMembers.filter(
        (item) => item !== userId
      );
    } else {
      this.selectedGroupMembers.push(userId);
    }
    this.createGroupForm.get('userIds')?.setValue(this.selectedGroupMembers);
  }

  toggleProportionalSelection(userId: number, percent: number) {
    const index = this.proportionalMembers.findIndex(
      (memberObj) => memberObj.userId === userId
    );
    if (percent) {
      if (index !== -1) {
        this.proportionalMembers[index].percent = percent;
      } else {
        this.proportionalMembers.push({ userId: userId, percent: percent });
      }
    } else {
      if (index !== -1) {
        this.proportionalMembers.splice(index, 1);
      }
    }
    this.selectedGroupMembers = this.proportionalMembers.map((memberObj) => {
      console.log(memberObj.percent);
      return memberObj.userId;

    }
    );
    this.createGroupForm.get('userIds')?.setValue(this.selectedGroupMembers);
    this.createGroupForm
      .get('proportionalDebtsData')
      ?.setValue(this.proportionalMembers);
  }

  isChecked(id: number) {
    return this.selectedGroupMembers.find((userId) => userId === id)
      ? true
      : false;
  }

  goBack() {
    this.router.navigate(['/groupdetails', this.groupId]);
  }

  submitForm(): void {
    console.log(this.createGroupForm.value);

    this.expenseService
      .createExpense(this.createGroupForm.value)
      .subscribe(() => {
        this.router.navigate(['/groupdetails', this.groupId]);
      });
  }
  get name() {
    return this.createGroupForm.get('name');
  }
  get currency() {
    return this.createGroupForm.get('currency');
  }
  get amount() {
    return this.createGroupForm.get('amount');
  }
  get payerId() {
    return this.createGroupForm.get('payerId');
  }
  get category() {
    return this.createGroupForm.get('category');
  }
  get distributionType() {
    return this.createGroupForm.get('distributionType');
  }
  get userIds() {
    return this.createGroupForm.get('userIds');
  }
  get proportionalDebtsData() {
    return this.createGroupForm.get('proportionalDebtsData');
  }
}

function percentSumValidator(otherControlName: string): ValidatorFn {
  return (control: AbstractControl): ValidationErrors | null => {
    const otherControl = control?.parent?.get(otherControlName) as AbstractControl;
    let sum: number = 0;
    const value: { userId: number; percent: number }[] = control.value;
    if(!otherControl){
      return null;
    }

    if(otherControl.value === 'proportional'){
      value.forEach((memberObj) => {
        sum += memberObj.percent as number;
      });
      console.log("sum: ", sum);

      if (sum > 100) {
        return { tooMuch: true };
      } else if (sum < 100) {
        return { tooShort: true };
      }else{
        return null;
      }
    }

    return null;
  };
}
<div class="row mb-4 mt-4">
  <div class="col-auto">
    <button
      type="button"
      class="btn btn-primary rounded-circle"
      title="Back"
      (click)="goBack()"
    >
      <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
    </button>
  </div>
  <div class="col text-center">
    <span class="display-4">Add new expense</span>
  </div>
</div>
<div class="row">
  <div class="col"></div>
  <div class="col-md-5">
    <form [formGroup]="createGroupForm" (ngSubmit)="submitForm()">
      <div class="row mb-2">
        <div class="col">
          <div class="form-group">
            <label for="name">Expense name</label>
            <input
              type="text"
              class="form-control"
              id="name"
              name="name"
              formControlName="name"
              required
            />
            <div
              *ngIf="name?.invalid && (name?.dirty || name?.touched)"
              class="alert alert-danger"
            >
              <div *ngIf="name?.errors?.['required']">Name is required.</div>
            </div>
          </div>
        </div>
      </div>
      <div class="row mb-2">
        <div class="col">
          <div class="form-group">
            <label for="amount">Amount</label>
          <input
            type="number"
            min="0"
            onkeypress="return event.charCode >= 48 && event.charCode <= 57"
            inputmode="numeric"
            class="form-control"
            placeholder="Spent amount"
            id="amount"
            name="amount"
            formControlName="amount"
          />
          <div
          *ngIf="amount?.invalid && (amount?.dirty || amount?.touched)"
          class="alert alert-danger"
        >
          <div *ngIf="amount?.errors?.['required']">Amount is required.</div>
        </div>
          </div>
        </div>
        <div class="col">
          <label for="currency">Currency</label>
          <select
            type="text"
            class="form-control"
            id="currency"
            name="currency"
            formControlName="currency"
            required
          >
            <option *ngFor="let currency of currencies" [value]="currency">
              {{ currency }}
            </option>
          </select>
          <div
          *ngIf="currency?.invalid && (currency?.dirty || currency?.touched)"
          class="alert alert-danger"
        >
          <div *ngIf="currency?.errors?.['required']">Currency is required.</div>
        </div>
        </div>
      </div>
      <div class="row mb-2">
        <div class="col">
          <div class="form-group">
            <label for="category">Expense category</label>
            <select
              class="form-select"
              aria-label="Default select example"
              id="category"
              name="category"
              formControlName="category"
            >
              <option *ngFor="let category of categories" [value]="category">
                {{ category }}
              </option>
            </select>
            <div
              *ngIf="category?.invalid && (category?.dirty || category?.touched)"
              class="alert alert-danger"
            >
              <div *ngIf="category?.errors?.['required']">Category is required.</div>
            </div>
          </div>
        </div>
      </div>
      <div class="row mb-2">
        <div class="col">
          <div class="form-group">
            <label for="description">Description</label>
            <textarea
              name="description"
              title="description"
              id="description"
              class="form-control"
              placeholder="Give a description..."
              formControlName="description"
            ></textarea>
          </div>
        </div>
      </div>     
      <div class="row mb-2">
        <div class="col-auto">
          <p>Paid by</p>
        </div>
        <div class="col">
          <select class="form-select" title="payer" formControlName="payerId" required>
            <option
              *ngFor="let groupMember of groupMembers"
              [value]="groupMember.id"
            >
              {{ groupMember.username }}
            </option>
          </select>
          <div
          *ngIf="payerId?.invalid && (payerId?.dirty || payerId?.touched)"
          class="alert alert-danger"
        >
        <div *ngIf="payerId?.errors?.['required']">You have to select, who paid</div>
        <div *ngIf="payerId?.errors?.['minLength']">You have to select, who paid</div>
      </div>
        </div>
        <div class="col-auto">
          <p>and split</p>
        </div>
        <div class="col">
          <select
            class="form-select"
            title="splittingMethod"
            formControlName="distributionType"
          >
            <option
              *ngFor="let distributionType of distributionTypes"
              [value]="distributionType"
            >
              {{ distributionType }}
            </option>
          </select>
          <div
          *ngIf="distributionType?.invalid && (distributionType?.dirty || distributionType?.touched)"
          class="alert alert-danger"
        >
          <div *ngIf="distributionType?.errors?.['required']">Select a type of distribution</div>
        </div>
        </div>
      </div>
      <div class="row  mb-2">
        <div class="col">
          <ng-container *ngIf="distributionType?.value == 'equal'">
            <ul class="list-group">
              <ng-container *ngFor="let groupMember of groupMembers">
                <input type="checkbox" class="btn-check" id="checkbox{{groupMember.id}}" autocomplete="off">
                <label class="form-check-label" for="checkbox{{groupMember.id}}">
                  <button type="button" class="list-group-item list-group-item-action" [ngClass]="{'active': isChecked(groupMember.id)}" (click)="toggleSelection(groupMember.id)">{{groupMember.username}}</button>
                </label>
              </ng-container>
            </ul>
          </ng-container>
          <ng-container *ngIf="distributionType?.value == 'proportional'">
            proportional cards to be made
            <ng-container *ngFor="let groupMember of groupMembers">
              <div class="form-check form-check-reverse card mb-2">
                <div class="card-body d-flex justify-content-between">                
                  {{ groupMember.username }}
                  <div class="input-group">
                    <input type="number" class="form-control" id="groupMember.id" aria-label="proportion" (input)="toggleProportionalSelection(groupMember.id, $any($event).target.value)">
                    <span class="input-group-text" id="{{groupMember.id}}">%</span>
                  </div>
                </div>
              </div>
            </ng-container>
          </ng-container>
          <ng-container *ngIf="distributionType?.value == 'exact_amounts'">
            exact_amounts to be made
            <ng-container *ngFor="let groupMember of groupMembers">
              <div class="form-check form-check-reverse card mb-2">
                <div class="card-body d-flex justify-content-between">
                  <label class="form-check-label" for="userIds">
                    {{ groupMember.username }}
                  </label>
                  <input
                    class="form-check-input"
                    type="checkbox"
                    [value]="groupMember.id"
                    id="userIds"
                    (change)="toggleSelection(groupMember.id)"
                  />
                </div>
              </div>
            </ng-container>
          </ng-container>
          <div
          *ngIf="userIds?.invalid || (distributionType?.value =='proportional' && proportionalDebtsData?.invalid)"
          class="alert alert-danger"
        >
          <div *ngIf="userIds?.errors?.['required']">You have to choose at least one person</div>
          <div *ngIf="userIds?.errors?.['minLength']">You have to choose at least one person</div>
          <div *ngIf="proportionalDebtsData?.errors?.['tooMuch']">You cant to distribute more than 100%</div>
          <div *ngIf="proportionalDebtsData?.errors?.['tooShort'] && distributionType?.value=='proportional'">You have to distribute 100%</div>
        </div>
        <div>{{createGroupForm.invalid}}</div>
        </div>
      </div>
      <button type="submit" class="btn btn-primary" [disabled]="!createGroupForm.valid">
        Create
      </button>
    </form>
  </div>
  <div class="col"></div>
</div>
