import { Component } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css'],
})
export class CardComponent {
  amount = 300;
  currency = 'HUF';
  title = 'Borok';
  payerName = 'Bede';
}
