import { Component, OnInit } from '@angular/core';
import { ExpenseService } from 'src/app/services/expense.service';
import { ExpenseDTO } from './dto';
import { Router } from '@angular/router';

@Component({
  selector: 'app-expenses',
  templateUrl: './expenses.component.html',
  styleUrls: ['./expenses.component.css'],
})
export class ExpensesComponent implements OnInit {
  navigateToExpense(id: number) {
    this.router.navigate(['/expensedetails', id])
  }
  expenses!: ExpenseDTO[];
  constructor(private expenseService: ExpenseService, private router: Router) {}
  ngOnInit(): void {
    this.expenseService.getMyExpenses().subscribe((result) => {
      this.expenses = result;
      console.log('expenses: ', this.expenses);
    });
  }
  // expenses = [
  //   {
  //     id: 1,
  //     expenseAmount: 2500,
  //     youOwe: 300,
  //     currency: 'HUF',
  //     groupName: 'Borok',
  //     payerName: 'Bede',
  //     distributionType: 'equally',
  //   },
  //   {
  //     id: 2,
  //     expenseAmount: 10000,
  //     youOwe: 2000,
  //     currency: 'HUF',
  //     groupName: 'Jegyek',
  //     payerName: 'Csilla',
  //     distributionType: 'equally',
  //   },
  //   {
  //     id: 1,
  //     expenseAmount: 5500,
  //     youOwe: 900,
  //     currency: 'HUF',
  //     groupName: 'Fagyi',
  //     payerName: 'Peti',
  //     distributionType: 'exactly',
  //   },
  // ];
}
<div class="row">
  <div class="row mb-4">
    <div class="col">
      <div class="d-flex justify-content-center">
        <span class="display-4">Expenses</span>
      </div>
    </div>
  </div>
  <div class="row d-flex justify-content-center">
      <div class="col-md-8">
        <ng-container *ngIf="expenses.length > 0; else emptyBlock">
          <ng-container *ngFor="let expense of expenses">
            <app-expense-menu-card
            [name]="expense.name"
            [amount]="expense.amount"
            [currency]="expense.currency"
            [distributionType]="expense.distributionType"
            [groupName]="expense.group.name"
            [id]="expense.group.id"
            (click)="navigateToExpense(expense.id)"
            ></app-expense-menu-card>
          </ng-container>
        </ng-container>
        <ng-template #emptyBlock>
          <p class="d-flex justify-content-center text-muted">There are no expenses</p>
        </ng-template>
      </div>
  </div>
</div>
