import { Location } from '@angular/common';
import { Component, OnDestroy, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { GroupService } from 'src/app/services/group.service';
import { ToastService } from 'src/app/services/toast.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-create-group',
  templateUrl: './create-group.component.html',
  styleUrls: ['./create-group.component.css'],
})
export class CreateGroupComponent implements OnInit, OnDestroy {
  createGroupForm = new FormGroup({
    groupName: new FormControl('', Validators.required),
    userIds: new FormControl<number[]>([]),
  });
  users!: { id: number; username: string }[];
  selectedUsers: number[] = [];
  constructor(
    private location: Location,
    private userService: UserService,
    private groupService: GroupService,
    private router: Router,
    private toastService: ToastService
  ) {}

  @ViewChild("successTpl", {read: TemplateRef}) successTemplate!: TemplateRef<any>;
  @ViewChild("dangerTpl", {read: TemplateRef}) failTemplate!: TemplateRef<any>;

  ngOnInit(): void {
    this.userService.getUsers().subscribe((result) => {
      console.log('result: ', result);
      this.users = result;
    });
  }
  submitForm(): void {
    console.log('Form data:', this.createGroupForm.value);
    this.groupService.createGroup(this.createGroupForm.value).subscribe(() => {
      this.showSuccessToast(this.successTemplate);
      // this.router.navigate(['/groups']);
    },
    (error)=>{
      this.showDangerToast(this.failTemplate);
    }
    );
    // Add your form submission logic here
  }
  goBack() {
    this.location.back();
  }

  toggleSelection(userId: number) {
    if (this.selectedUsers.includes(userId)) {
      this.selectedUsers = this.selectedUsers.filter((item) => item !== userId);
    } else {
      this.selectedUsers.push(userId);
    }
    this.createGroupForm.get('userIds')?.setValue(this.selectedUsers);
    console.log('this.selectedGroupMembers: ', this.selectedUsers);
  }

  showSuccessToast(template: TemplateRef<any>) {
		this.toastService.show({ template, classname: 'bg-success text-light', delay: 10000 });
	}

  showDangerToast(template: TemplateRef<any>) {
		this.toastService.show({ template, classname: 'bg-danger text-light', delay: 15000 });
	}

  get userIds() {
    return this.createGroupForm.get('userIds');
  }

  get groupName() {
    return this.createGroupForm.get('groupName');
  }

  ngOnDestroy(): void {
    this.toastService.clear();
  }
}

<div class="row mb-4 mt-4">
  <div class="col-auto">
    <button
      type="button"
      class="btn btn-primary rounded-circle"
      title="Back"
      (click)="goBack()"
    >
      <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
    </button>
  </div>
  <div class="col text-center">
    <span class="display-4">Create new group</span>
  </div>
</div>
<div class="row">
  <div class="col"></div>
  <div class="col-md-5">
    <form [formGroup]="createGroupForm" (ngSubmit)="submitForm()">
      <div class="row mb-2">
        <div class="col">
          <div class="form-group">
            <label for="groupName">Group name</label>
            <input
              type="text"
              class="form-control"
              id="groupName"
              name="groupName"
              formControlName="groupName"
              required
            />
            <div
              *ngIf="
                groupName?.invalid && (groupName?.dirty || groupName?.touched)
              "
              class="alert alert-danger"
            >
              <div *ngIf="groupName?.errors?.['required']">
                Name is required.
              </div>
            </div>
          </div>
        </div>
      </div>
      <div class="row mb-2">
        <div class="col">
          <ng-container *ngIf="true">
            <!-- TODO: ng bootstrap accordionba tenni, így összecsukható a rengeteg user? -->
            <div>
              <p>Add members</p>
            </div>
            <ng-container *ngFor="let user of users">
              <div class="form-check form-check-reverse card mb-2">
                <div class="card-body d-flex justify-content-between">
                  <label class="form-check-label" for="userIds">
                    {{ user.username }}
                  </label>
                  <input
                    class="form-check-input"
                    type="checkbox"
                    [value]="user.id"
                    id="userIds"
                    (change)="toggleSelection(user.id)"
                  />
                </div>
              </div>
            </ng-container>
          </ng-container>
          <div>
            <span class="text-muted"
              >If you don't select any members now, you will be the only member
              of the group. You can add members later.</span
            >
          </div>
        </div>
      </div>
      <button
        type="submit"
        class="btn btn-primary"
        [disabled]="!createGroupForm.valid"
      >
        Create
      </button>
    </form>
  </div>
  <div class="col"></div>
</div>
<button
        type="button"
        class="btn btn-primary"
        (click)="showSuccessToast(successTpl)"
      >
<ng-template #successTpl> I'm a success toast </ng-template>
<ng-template #dangerTpl>
	<svg xmlns="http://www.w3.org/2000/svg" fill="#fff" width="24" height="24" viewBox="0 0 24 24">
		<path
			d="M10.872 6.831l1.695 3.904 3.654-1.561-1.79 3.426 3.333.954-3.417 1.338 2.231 4.196-4.773-2.582-2.869 2.287.413-3.004-3.792-.726 2.93-1.74-1.885-2.512 3.427.646.843-4.626zm-.786-6.831l-1.665 9.119-6.512-1.228 3.639 4.851-5.548 3.294 7.108 1.361-.834 6.076 5.742-4.577 9.438 5.104-4.288-8.064 6.834-2.677-6.661-1.907 3.25-6.22-6.98 2.982-3.523-8.114z"
		/>
	</svg>
	Danger Danger !
</ng-template>
