import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Route } from '@angular/router';
import { GroupService } from 'src/app/services/group.service';
import { GroupDTO } from '../dto/group.dto';

@Component({
  selector: 'app-edit-group',
  templateUrl: './edit-group.component.html',
  styleUrls: ['./edit-group.component.css'],
})
export class EditGroupComponent implements OnInit {
  id!: number;
  groupDetails!: GroupDTO;
  editGroupForm = new FormGroup({
    name: new FormControl('', Validators.required),
    userIds: new FormControl<number[]>([]),
  });
  constructor(
    private location: Location,
    private route: ActivatedRoute,
    private groupService: GroupService
  ) {}

  ngOnInit(): void {
    this.id = +this.route.snapshot.paramMap.get('id')!;
    console.log('id: ', this.id);
    this.groupService
      .getGroupById(this.id)
      .subscribe((response) => this.groupDetails = response);
      this.editGroupForm.get('name')?.setValue(this.groupDetails.name);
      this.editGroupForm.get('userIds')?.setValue(this.groupDetails.members.map(member=> member.id));
      console.log(this.editGroupForm.get('userIds')?.value);
  }
  goBack() {
    this.location.back();
  }
}
