import { Component } from '@angular/core';
import { FormControl, FormGroup, NgForm, Validators } from '@angular/forms';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';

const ROLE_ADMIN: string = 'admin';
const ROLE_USER: string = 'user';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  constructor(private authService: AuthService, private router: Router) {}

  loginForm2 = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required]),
  });
  login(loginForm: NgForm) {
    console.log(loginForm.value);
    console.log('form is submitted');
    this.authService.login(loginForm.value).subscribe(
      (response: any) => {
        const token = response.token;
        const role = response.role;
        this.authService.setToken(response.token);
        this.authService.setRoles(response.role);
        if (role === ROLE_ADMIN) {
          this.router.navigate(['/admin']);
        } else {
          this.router.navigate(['/groups']);
        }
      },
      (error) => {
        console.log(error.message);
      }
    );
  }

  submitForm() {
    this.authService.login(this.loginForm2.value).subscribe(
      (response: any) => {
        const role = response.role;
        this.authService.setToken(response.token);
        this.authService.setRoles(response.role);
        if (role === ROLE_ADMIN) {
          this.router.navigate(['/admin']);
        } else {
          this.router.navigate(['/groups']);
        }
      },
      (error) => {
        if (error.status === 403) {
          this.loginForm2.get('email')?.setErrors({ badCredentials: true });
        }
      }
    );
  }

  get email() {
    return this.loginForm2.get('email');
  }
}
<div class="row mb-4">
  <div class="col">
    <div class="d-flex justify-content-center">
      <span class="display-4">Login</span>
    </div>
  </div>
</div>
<div class="row d-flex justify-content-center">
  <div class="col-md-4">
    <form #loginForm="ngForm" (ngSubmit)="login(loginForm)">
      <div class="row">
        <div class="col">
          <input
          type="email"
          ngModel
          name="email"
          id="email"
          placeholder="Enter your email"
          class="form-control mb-3"
        />
        </div>
      </div>
      <div class="row">
        <div class="col">
          <input
          type="password"
          ngModel
          name="password"
          id="password"
          placeholder="Enter your password"
          class="form-control mb-3"
        />
        </div>
      </div>
      <div class="row">
        <div class="col">
          <input type="submit" class="btn btn-primary form-control" value="Login" />
        </div>
      </div>
    </form>
    <form [formGroup]="loginForm2" (ngSubmit)="submitForm()">
      <div class="row mb-2">
        <div class="col">
          <div class="form-group">
            <label for="email">Email</label>
            <input
              type="text"
              class="form-control"
              id="email"
              name="email"
              formControlName="email"
              required
            />
            <div
              *ngIf="
                email?.invalid && (email?.dirty || email?.touched)
              "
              class="alert alert-danger"
            >
              <div *ngIf="email?.errors?.['required']">
                Name is required.
              </div>
            </div>
          </div>
        </div>
      </div>
    </form>
  </div>
</div>
