import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  constructor(private http: HttpClient) {}
  private baseUrl: string = 'http://localhost:3000/auth';

  login(logindata: any) {
    return this.http.post(this.baseUrl + '/signin', logindata);
  }

  public setRoles(roles: []) {
    localStorage.setItem('roles', JSON.stringify(roles));
  }

  public getRoles() {
    return localStorage.getItem('roles');
  }

  public setToken(jwtToken: string) {
    localStorage.setItem('jwtToken', jwtToken);
  }

  public getToken(): string | null {
    return localStorage.getItem('jwtToken');
  }

  public clear() {
    localStorage.clear();
  }

  public isLoggedIn() {
    return this.getRoles() && this.getToken();
  }

  public roleMatch(allowedRoles: string[]): boolean {
    let isRoleMatching = false;
    const userRoles: any = this.getRoles();
    if (userRoles) {
      userRoles.array.forEach((userRole: string) => {
        isRoleMatching = allowedRoles.find((role: string) => {
          return role === userRole;
        });
        allowedRoles.includes
      });
    }
  }
}
