import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-debts-menu-card',
  templateUrl: './debts-menu-card.component.html',
  styleUrls: ['./debts-menu-card.component.css'],
})
export class DebtsMenuCardComponent {
  @Input() userId!: number;
  @Input() amount!: number;
  @Input() currency!: string;
  @Input() payerId!: number;
  @Input() payerName!: string;
  @Input() groupId!: number;
  @Input() groupName!: string;
  @Input() expenseId!: number;
  @Input() expenseName!: string;

  constructor(private router: Router) {}

  navigateToGroup(id: number) {
    this.router.navigate(['/groupdetails', id]);
  }

  navigateToExpense(id: number) {
    this.router.navigate(['/expensedetails', id]);
  }
}
<div class="card mb-4">
  <div class="card-body">
    <div class="row align-items-center">
      <div class="col-auto">
        <div class="row">
          <i class="bi bi-cash-coin" style="font-size: 1.5rem"></i>
        </div>
      </div>
      <div class="col flex-d justify-content-center">
        <div class="row">
          You owe {{ amount }} {{ currency }} to {{ payerName }} in group <b (click)="navigateToGroup(groupId)">{{ groupName }}</b> for <b (click)="navigateToExpense(expenseId)">{{ expenseName }}</b>
        </div>
      </div>
    </div>
  </div>
</div>
