import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GroupService } from 'src/app/services/group.service';
import { GroupDetailsDTO } from '../dto';

@Component({
  selector: 'app-group-details',
  templateUrl: './group-details.component.html',
  styleUrls: ['./group-details.component.css'],
})
export class GroupDetailsComponent implements OnInit {
  id!: number;
  details!: GroupDetailsDTO;
  expenses!: {
    id: number;
    name: string;
    amount: number;
    currency: string;
    category: string;
    distribution: string;
    debtAmount: number | null;
    payerName: string;
  }[];
  members!: {
    id: number;
    username: string;
    firstName: string;
    lastName: string;
  }[];
  balanceOfUsers!: {
    userId: number;
    username: string;
    sumDebtsByCurrencies: { sumAmount: number; currency: string }[];
  }[];

  constructor(
    private groupService: GroupService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.id = +this.route.snapshot.paramMap.get('id')!;
    this.groupService.getGroupDetailsById(this.id).subscribe((result) => {
      this.details = result;
      this.expenses = this.details.expenses;
      this.members = this.details.members;
      this.balanceOfUsers = this.details.balanceOfUser;
    });
  }
  goBack() {
    this.router.navigate(['/groups']);
  }

  navigate() {
    this.router.navigate(['/group', this.id, 'addexpense']);
  }
}
<div class="row mb-4 mt-2">
  <div class="col-auto">
    <button type="button" class="btn btn-primary rounded-circle" title="Back" (click)="goBack()">
      <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
    </button>
  </div>
  <div class="col">
    <h1 class="text-center">{{ details.name }}</h1>
  </div>
  <!-- TODO: edit group funkc -->
  <div class="col-auto">
    <button class="btn btn-primary">Edit group</button>
  </div>
</div>
<div class="row mb-4">
  <div class="container d-flex justify-content-evenly">
    <div class="col-auto">
      Balance:
    </div>
    <div class="col-auto">
      <!-- <div *ngFor="let balance of balanceOfUsers">
        {{balance.username}} owes <ng-container *ngFor="let debt of balance.sumDebtsByCurrencies; let last = last">{{debt.sumAmount}} {{debt.currency}}<ng-container *ngIf="!last">, </ng-container></ng-container>
      </div> -->
      <div *ngFor="let balance of balanceOfUsers">
        <ng-container *ngIf="balance.sumDebtsByCurrencies.length > 0">
          {{ balance.username }} owes 
          <ng-container *ngFor="let debt of balance.sumDebtsByCurrencies; let last = last">
            <ng-container *ngIf="debt.sumAmount >= 0; else negativeDebt">
              {{ debt.sumAmount }} {{ debt.currency }}
            </ng-container>
            <ng-template #negativeDebt>
              you owe {{ -debt.sumAmount }} to {{ balance.username }}
            </ng-template>
            <ng-container *ngIf="!last">, </ng-container>
          </ng-container>
        </ng-container>
      </div>
    </div>
  </div>
</div>
<div class="row">
  <div class="col-md">
    <div class="row">
      <div class="card">
        <div class="card-body">
          <div class="card-title text-center">
            <h5>Members:</h5>
          </div>
          <div class="card-text">
            <div *ngFor="let member of members">
              <div class="row mb-2">
                <div class="col-md text-center">
                  <div
                    *ngIf="member.firstName && member.lastName; else usernameBlock"
                  >
                    {{ member.firstName }} {{ member.lastName }}
                  </div>
                </div>
                <ng-template #usernameBlock>
                  {{ member.username }}
                </ng-template>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  <div class="col-md-8">
    <ng-container *ngIf="details.expenses; else emptyBlock">
      <div class="card-deck" *ngFor="let expense of details.expenses">
        <app-expense-card
          [id]="expense.id"
          [payerName]="expense.payerName"
          [currency]="expense.currency"
          [distributionTypeKey]="expense.distribution"
          [expenseAmount]="expense.amount"
          [expenseName]="expense.name"
          [youOwe]="expense.debtAmount"
        ></app-expense-card>
      </div>
    </ng-container>
    <ng-template #emptyBlock>
      <p class="text-muted">There are no expenses</p>
    </ng-template>

  </div>
  <div class="col-md">
    <p>TODO: itt lesznek a utility gombok asszem</p>
  </div>
</div>
  <!-- TODO: add expense funkc -->
<div class="fixedButton">
  <button type="button" class="btn btn-primary" (click)="navigate()">Add expense</button>
</div>
