import { Component } from '@angular/core';

@Component({
  selector: 'app-create-expense',
  templateUrl: './create-expense.component.html',
  styleUrls: ['./create-expense.component.css']
})
export class CreateExpenseComponent {
  formData = {
    expenseName: '',
  };
  submitForm(): void {
    console.log('Form data:', this.formData);
    // Add your form submission logic here
  }
}
<div class="row mb-4 mt-4">
    <span class="text-center display-4">Add new expense</span>
</div>
<div class="row">
    <div class="col-auto">
        <button type="button" class="btn btn-primary rounded-circle" title="Back" (click)="goBack()">
          <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
        </button>
      </div>
<div class="col">

</div>
<div class="col-md-5">
    <form>
        <div class="form-group mb-2">
          <label for="name">Expense name</label>
          <input type="text" class="form-control" id="name" name="name" [(ngModel)]="formData.expenseName">
        </div>
        <div class="form-group mb-2">
          <label for="email">Group members</label>
          <!-- TODO: add add user logic -->
          <input type="email" class="form-control" id="email" name="email" disabled value="here gonna be the add members section">
        </div>
        <button type="submit" class="btn btn-primary" (click)="submitForm()">Create</button>
      </form>
</div>
<div class="col"></div>
</div>
