import { Location } from '@angular/common';
import { Component, Input } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-create-group',
  templateUrl: './create-group.component.html',
  styleUrls: ['./create-group.component.css'],
})
export class CreateGroupComponent {
  formData = {
    groupName: '',
  };
  constructor(private location: Location) {}
  submitForm(): void {
    console.log('Form data:', this.formData);
    // Add your form submission logic here
  }
  goBack() {
    this.location.back();
  }
}
<div class="row mb-4 mt-4">
  <div class="col">
    <div class="col-auto">
      <button type="button" class="btn btn-primary rounded-circle" title="Back" (click)="goBack()">
        <i class="bi bi-arrow-left" style="font-size: 1.5rem; color: white"></i>
      </button>
    </div>
  </div>
  <div class="col">
    <span class=" display-4">Create new group</span>

  </div>
</div>
<div class="row">
<div class="col"></div>
<div class="col-md-5">
    <form>
        <div class="form-group mb-2">
          <label for="name">Group name</label>
          <input type="text" class="form-control" id="name" name="name" [(ngModel)]="formData.groupName">
        </div>
        <div class="form-group mb-2">
          <label for="email">Group members</label>
          <!-- TODO: add add user logic -->
          <input type="email" class="form-control" id="email" name="email" disabled value="here gonna be the add members section">
        </div>
        <app-find-user-typeahead></app-find-user-typeahead>
        <button type="submit" class="btn btn-primary" (click)="submitForm()">Create</button>
      </form>
</div>
<div class="col"></div>
</div>
